import React, { Component } from "react";


// fake data
const roles = [
	{
		'id': 0,
		'role': 'Operador maquina',
	},
	{
		'id': 1,
		'role': 'Analista madera',
	}

];

const activities = [

	{'activity': 'Ir a tomar muestra al equipo 1'},
	{'activity': 'Ir a dejar muestra de equipo 1 a laboratorio'},
]


class Control_Risk extends Component{

	// constructor
	constructor(props) {

		// constructur of parent
		super(props);

		// initial states
		this.state = {

			// this should be false at beganing
			get_data: true,

			// this should be empty at beganing 
			roles: roles,

		}

	}

	render() {

		return (

			<div className = 'container'>

				<div className = 'container'>

					{/* select role */}
					<select>

						{roles.map( (role, idx) =>

							<option value = {role.id}> {role.role} </option>

						)}

					</select>

				</div>

				<div className = 'container'>

					{/* select activity */}
					<select>

						{activities.map( (activity, idx) =>

							<option value = {activity.activity}> {activity.activity} </option>

						)}

					</select>

				</div>

			</div>

		);

	}

}

export default Control_Risk;
