import React, { Component } from "react";


// fake data
const roles = [
	'Operador maquina',
	'Operador caldera',
	'Analista madera',
	'Analista RILES',
];


class Control_Risk extends Component{

	// constructor
	constructor(props) {

		// constructur of parent
		super(props);

		// initial states
		this.state = {

			num_loteos: null,
			num_pieces_ground: 39,
			// flag of get data from server
			get_loteos: false,
			// markers (each place is a list of 2 elements)
			// marker: [latitude, longitude]
			loteos: null,

		}

	}

	render() {

		return (

			<div className = 'container'>

				<select>

					{roles.map( (rol, idx) =>

						<option value = {rol}> {rol} </option>

					)}

				</select>

			</div>

		);

	}

}

export default Control_Risk;
